import React, { Component } from 'react';
import './App.css';
import data from './data.json';
const categoryData = data.categoryList;
const productData = data.productList;

var active_filters = [];

class Categories extends Component
{
  render() {
    return (
      <>
        <h2>Categories</h2>
        <div id="categories">
          {categoryData.map((category,i) => (
            <div key={i}>
              <input type="checkbox" className="filter" id={category} value={category}></input>
              <label htmlFor={category}>{category}</label>
              <br></br>
            </div>
          ))}
        </div>
        <button onClick={apply_filter}>Apply Filter</button>
      </>
    );
  }
}

class Shop extends Component
{
  render() {
    return (
      <>
        <h2>Shop</h2>
        <table id="shop">
          <tbody>
          {productData.map((product, i) => (
            <tr key={i} id={product.category}>
              <form>
              <td>{product.name}</td>
              <td>{product.category}</td>
              <td>{product.price}</td>
              <td> <img src={process.env.PUBLIC_URL + "/images/"+product.link} alt={product.name}/></td>
              <td><input type="submit" value="Info"></input></td>
              </form>
            </tr>
          ))}
          </tbody>
        </table>
      </>
    );
  }
}

class App extends Component {

  render() {
    return (

      <div className="App">

        <header className="shop_header">
          <h1>Game Shop</h1>
        </header>

        <nav>
          <a href="game_Shop.html">Shop</a>
          <a href="basket.html">Basket</a>
          <a href="login.html">Login</a>
        </nav>

        <aside className="side_bar">
          <Categories/>
        </aside>

        <section className="shop_section">
          <Shop/>
        </section>

        <footer className="shop_footer">
          <p> Best games for best prices</p>
        </footer>

      </div>
    );
  }

}

function apply_filter()
{
  var filter_list = document.getElementsByClassName("filter")

  active_filters = []

  for(var i=0; i<filter_list.length;++i) 
  {
    if(filter_list[i].checked) active_filters.push(filter_list[i])
  }

  console.log(filter_list)

  console.log(active_filters)



}

function display_info(item_id)
{
  console.log(item_id)


}

export default App;
