import React, { Component } from 'react';
import './App.css';
import data from './data.json';
const categoryData = data.categoryList;
const productData = data.productList;

class Categories extends React.Component
{
  render() {
    return (
      <>
        <h2>Categories</h2>
        <div id="categories">
          {categoryData.map(category => (
            <>
              <input type="checkbox" class="filter" id={category} value={category}></input>
              <label for={category}>{category}</label>
              <br></br>
            </>
          ))}
        </div>
        <button onclick="apply_filter()">Apply Filter</button>
      </>
    );
  }
}

class Shop extends React.Component
{
  render() {
    return (
      <>
        <h2>Shop</h2>
        <table id="shop">
          {productData.map((product, i) => (
            <tr key={i}>
              <td>{product.name}</td>
              <td>{product.category}</td>
              <td>{product.price}</td>
              <td> <img src={process.env.PUBLIC_URL + '/images/gta5.jpg'} /> </td>
            </tr>
          ))}
        </table>
        <button onclick="apply_filter()">Apply Filter</button>
      </>
    );
  }
}

class App extends React.Component {

  render() {
    return (
      <div className="App">
        <head>
          <title>Game Shop</title>
        </head>

        <header class="shop_header">
          <h1>Game Shop</h1>
        </header>

        <nav class="shop_navigation">
          <a href="game_Shop.html">Shop</a>
          <a href="basket.html">Basket</a>
          <a href="login.html">Login</a>
        </nav>

        <aside class="side_bar">
          <Categories/>
        </aside>

        <section class="shop_section">
          <Shop/>
        </section>

        <footer class="shop_footer">
          <p> Best games for best prices</p>
        </footer>

      </div>
    );
  }

}

export default App;
