import React, { Component } from 'react';
import './App.css';

class Categories extends React.Component {
  render() {
    return (
      <h2> Hello Categories</h2>

    );
  }
}

class App extends React.Component {
  render() {
    return (
      <div className="App">
        <head>
          <title>Game Shop</title>
        </head>

        <header class="shop_header">
          <h1>Game Shop</h1>
        </header>

        <nav class="shop_navigation">
          <a href="game_Shop.html">Shop</a>
          <a href="basket.html">Basket</a>
          <a href="login.html">Login</a>
        </nav>

        <aside class="side_bar">
          <h2>Categories</h2>
          <div id="categories"></div>
          <button onclick="apply_filter()">Apply Filter</button>
          Categories
        </aside>

        <main class="shop_main">
          <h1 id="label_shop">Shop</h1>

          <div class="flex-container" id="Items"></div>

          <script>fill_items()</script>
        </main>

        <footer class="shop_footer">
          <p> Best games for best prices</p>
        </footer>

      </div>
    );
  }
}

var category_list = ["Rockstar","Origin","Steam","Epic Games"]
function fill_category()
{
    var text = "";
    for (var i=0; i<category_list.length; i++)
    {
        text += "<input type=checkbox" + " class=filter" + " id=" + category_list[i] + " value=" + category_list[i] + ">"
        text += "<label for=" + category_list[i] + ">" + category_list[i] + "</label> <br>"
    }
    document.getElementById("categories").innerHTML = text;
}

export default App;
