import React, { Component } from 'react';
import './App.css';
import data from './data.json';
const categoryData = data.categoryList;
const productData = data.productList;

var active_filters = [];

class Categories extends Component
{
  constructor(props)
  {
    super(props);

    apply_filter = () => (this.props.apply_filter());
  }

  render() {
    return (
      <>
        <h2>Categories</h2>
        <div id="categories">
          {categoryData.map((category,i) => (
            <div key={i}>
              <input type="checkbox" className="filter" id={category} value={category}></input>
              <label htmlFor={category}>{category}</label>
              <br></br>
            </div>
          ))}
        </div>
        <button onClick={this.apply_filter}>Apply Filter</button>
      </>
    );
  }
}


class Shop extends Component
{
  constructor(props)
  {
    super(props);
    this.apply_filter = this.apply_filter.bind(this);
  }

  apply_filter() {
    var filter_list = document.getElementsByClassName("filter")

    active_filters = []

    for (var i = 0; i < filter_list.length; ++i) {
      if (!filter_list[i].checked) active_filters.push(filter_list[i].value)
    }

    console.log(filter_list)

    console.log(active_filters)
    this.parent.updateChild();
  }

  render() {
    return (
      <>
        <h2>Shop</h2>
          {productData.filter((product) =>!(product.category in active_filters)).map((product, i) => (
            <form key={i} id={product.name} action="/info.html">
              <p>{product.name}</p> 
              <p>Category: {product.category}</p>
              <p>Price: {product.price}</p>
              <input type="submit" value="Info"></input>
              <br></br>
              <br></br>
              <img src={process.env.PUBLIC_URL + "/images/"+product.link} alt={product.name}/>
            </form>
          ))}
      </>
    );
  }
}

class App extends Component {

  constructor(props)
  {
    super(props);
    this.state = { filtered: false};

    this.updateChild = this.updateChild.bind(this);

  }

  updateChild() 
  { 
    this.setState(state => ({filtered : !state.filtered}));
  }

  render() {
    return (

      <div className="App">

        <header className="shop_header">
          <h1>Game Shop</h1>
        </header>

        <nav>
          <a href="game_Shop.html">Shop</a>
          <a href="basket.html">Basket</a>
          <a href="login.html">Login</a>
        </nav>

        <aside className="side_bar">
          <Categories/>
        </aside>

        <section className="shop_section">
          <Shop/>
        </section>

        <footer className="shop_footer">
          <p> Best games for best prices</p>
        </footer>

      </div>
    );
  }

}




