import React, { Component } from 'react';
import './App.css';
import data from './data.json';

const categoryData = data.categoryList;

class Categories extends React.Component
{
  render() {
    return (
      <>
        <h2>Categories</h2>
        <div id="categories">
          {categoryData.map(category => (
            <>
              <input type="checkbox" class="filter" id={category} value={category}></input>
              <label for={category}>{category}</label>
              <br></br>
            </>
          ))}
        </div>
        <button onclick="apply_filter()">Apply Filter</button>
      </>
    );
  }
}

class App extends React.Component {

  render() {
    return (
      <div className="App">
        <head>
          <title>Game Shop</title>
        </head>

        <header class="shop_header">
          <h1>Game Shop</h1>
        </header>

        <nav class="shop_navigation">
          <a href="game_Shop.html">Shop</a>
          <a href="basket.html">Basket</a>
          <a href="login.html">Login</a>
        </nav>

        <aside class="side_bar">
          <Categories/>
        </aside>

        <main class="shop_main">
          <h1 id="label_shop">Shop</h1>

          <div class="flex-container" id="Items"></div>

          <script>fill_items()</script>
        </main>

        <footer class="shop_footer">
          <p> Best games for best prices</p>
        </footer>

      </div>
    );
  }

}

export default App;
