import React, { Component } from 'react';
import data from "../Data/data.json";
import {useHistory} from "react-router-dom";
import * as functions from "../functions";

import {
  Container, Col, Form,
  FormGroup, Label, Input,
  Button, Row,
} from 'reactstrap';


class SignUp extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      username: '',
      age: null,
    };
  }
  mySubmitHandler = (event) => {
    event.preventDefault();

    let email = this.state.email;
    let password = this.state.password;
    let rePassword = this.state.rePassword
    let firstName = this.state.firstName;
    let lastName = this.state.lastName;

		if(!email.includes('@')){
			alert("Please enter a valid e-mail.")
		}
		else if(!email.includes("edu.tr") && !email.includes("email.com") && !email.includes("gmail.com")){
			alert("Please enter edu.tr, gmail.com or email.com")
		}
		else if(password.length < 6 || rePassword.length < 6) {
			alert("Password size shoul be at least 6.")
		}
		else if(!hasUpperCase(password) || !hasUpperCase(rePassword)) {
			alert("Passwords should include at least on upper case")
		}
	    else if(password !== rePassword){
			alert("Passwords do not match.")
		}



  }
  myChangeHandler = (event) => {
    let nam = event.target.name;
    let val = event.target.value;
    this.setState({[nam]: val});
  }
  render() {
    return (
      <form onSubmit={this.mySubmitHandler}>

      <h1>Hello {this.state.username} {this.state.age}</h1>

      <p>Email:</p>

      <input type='text' name='email' onChange={this.myChangeHandler} />

      <p>Password</p>
      <input type='text' name='password' onChange={this.myChangeHandler} />

      <p>Re-Password</p>
      <input type='text' name='rePassword' onChange={this.myChangeHandler} />

      <p>First Name</p>
      <input type='text' name='firstName' onChange={this.myChangeHandler} />

      <p>Last Name</p>
      <input type='text' name='lastName' onChange={this.myChangeHandler} />      

      <br/><br/>

      <input type='submit' />

      </form>
    );
  }
}


const MyForm = () => {
  const history = useHistory();
    const goToPage = (pageName) => history.push(pageName);
    const productData = data.productList;

    const goToLoginPage = () => {
        goToPage('Login')
    }

	 const goToShopPage = () => {
        goToPage('')
    }

	const validate = (mail, pass1, pass2) => {
		if(!mail.includes('@')){
			alert("Please enter a valid e-mail.")
		}
		else if(!mail.includes("edu.tr") && !mail.includes("email.com") && !mail.includes("gmail.com")){
			alert("Please enter edu.tr, gmail.com or email.com")
		}
		else if(pass1.length < 6 || pass2.length < 6) {
			alert("Password size shoul be at least 6.")
		}
		else if(!hasUpperCase(pass1) || !hasUpperCase(pass2)) {
			alert("Passwords should include at least on upper case")
		}
	    else if(pass1 != pass2){
			alert("Passwords do not match.")
		}
		else {

			goToShopPage()
		}
    }

	function hasUpperCase(str) {
		return str.match(/[A-Z]/);
	}

    return (
      <Container className="SignUp">
        <h2>Sign In</h2>
        <Form className="form">
          <Col>
            <FormGroup>
              <Label>Email</Label>
              <Input
                type="email"
                name="email"
                id="exampleEmail1"
                placeholder="myemail@email.com"
              />
            </FormGroup>
          </Col>
          <Col>
            <FormGroup>
              <Label for="examplePassword1">Password</Label>
              <Input
                type="password"
                name="password1"
                id="examplePassword1"
                placeholder="********"
              />
            </FormGroup>
          </Col>
		  <Col>
            <FormGroup>
              <Label for="examplePassword2">Re-type Password</Label>
              <Input
                type="password"
                name="password2"
                id="examplePassword2"
                placeholder="********"
              />
            </FormGroup>
          </Col>
          <Row className='fullWidth'>
				<Col md="6">
					<Button onClick={() => validate(document.getElementById('exampleEmail1').value, document.getElementById('examplePassword1').value, document.getElementById('examplePassword2').value)} className="mt-3 btn btn-info btn-product text-nowrap" >Sign Up</Button>
				</Col>
				<Col md="6">
					<Button onClick={() => goToLoginPage()} className="mt-3 btn btn-success btn-product text-nowrap">Login</Button>
				</Col>
			</Row>
        </Form>
      </Container>
    );
}

export default SignUp
